import { useState } from "react";
import { Pressable, View } from "react-native";
import { Svg, Rect, Defs, Pattern, Use, Image } from "react-native-svg";
import { SocialLoginIconProps, LoginIconReturnType, LoginIconProps, PressableIconProps } from "./types";

function GoogleLoginIcon(props: LoginIconProps): LoginIconReturnType {
    const { diameter } = props;
    const radius = diameter / 2;

    return (
        <Svg
            width={diameter}
            height={diameter}
            viewBox={`0 0 ${diameter.toString()} ${diameter.toString()}`}
            fill="none">
            <Rect width={diameter} height={diameter} rx={radius} fill="url(#pattern0)" />
            <Defs>
                <Pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1">
                    <Use xlinkHref="#image0_163_749" transform="scale(0.0125)" />
                </Pattern>
                <Image
                    id="image0_163_749"
                    width="80"
                    height="80"
                    xlinkHref="data:image/png;base64,"
                />
            </Defs>
        </Svg>
    );
}

