function PressableIcon(props: PressableIconProps): JSX.Element {
    const { diameter } = props;
    const radius = diameter / 2;
    const pressedOpacity: number = 0.2;
    const unpressedOpacity: number = 0;
    const [opacity, setOpacity] = useState(unpressedOpacity);

    return (
        <Pressable
            onPress={props.onPress}
            onPressIn={() => (props.onPress ? setOpacity(pressedOpacity) : undefined)}
            onPressOut={() => (props.onPress ? setOpacity(unpressedOpacity) : undefined)}>
            {props.children}
            <View
                style={{
                    position: "absolute",
                    top: 0,
                    bottom: 0,
                    left: 0,
                    right: 0,
                    width: diameter,
                    height: diameter,
                    backgroundColor: "grey",
                    borderRadius: radius,
                    opacity: opacity,
                    zIndex: 1,
                }}
            />
        </Pressable>
    );
}

export default function SocialLoginIcon(props: SocialLoginIconProps): LoginIconReturnType {
    const { providerName, diameter } = props;

    switch (providerName) {
        case "kakao":
            return (
                <PressableIcon diameter={diameter} onPress={props.onPress}>
                    <KakaoLoginIcon diameter={diameter} />
                </PressableIcon>
            );
        case "naver":
            return (
                <PressableIcon diameter={diameter} onPress={props.onPress}>
                    <NaverLoginIcon diameter={diameter} />
                </PressableIcon>
            );
        default:
            return (
                <PressableIcon diameter={diameter} onPress={props.onPress}>
                    <GoogleLoginIcon diameter={diameter} />
                </PressableIcon>
            );
    }
}
